import React, { useState } from 'react';
import toast, { Toaster } from 'react-hot-toast';
import styles from '../styles/questionnaire.module.css';

export default function Quiz() {
  const [questionIndex, setQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [isAnswered, setIsAnswered] = useState(false);
  const [answer, setAnswer] = useState('');
  const questions = [
    {
      text: 'What is the capital of France?',
      options: [
        { text: 'London', isCorrect: false },
        { text: 'Paris', isCorrect: true },
        { text: 'Madrid', isCorrect: false },
        { text: 'Berlin', isCorrect: false },
      ],
    },
    {
      text: 'What is the largest planet in our solar system?',
      options: [
        { text: 'Mars', isCorrect: false },
        { text: 'Venus', isCorrect: false },
        { text: 'Jupiter', isCorrect: true },
        { text: 'Saturn', isCorrect: false },
      ],
    },
    {
      text: 'What is the tallest mammal in the world?',
      options: [
        { text: 'Elephant', isCorrect: false },
        { text: 'Giraffe', isCorrect: true },
        { text: 'Hippopotamus', isCorrect: false },
        { text: 'Kangaroo', isCorrect: false },
      ],
    },
  ];

  const handleAnswer = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
      toast.success('Correct!');
    } else {
      toast.error('Incorrect!');
    }
    setIsAnswered(true);
  };

  const handleNext = () => {
    setIsAnswered(false);
    setAnswer('');
    setQuestionIndex(questionIndex + 1);
  };

  const currentQuestion = questions[questionIndex];

  return (
    <div className="container mx-auto">
      <Toaster position="top-center" reverseOrder={false} />
      <div className="flex justify-center items-center" style={{ marginTop: '3rem', marginBottom: '3rem' }}>
        <div className={styles.quiz}>
          <h5 className={styles.question}>{currentQuestion.text}</h5>
          <div className={styles.options}>
            {currentQuestion.options.map((option, index) => (
              <div key={index} className={styles.option} onClick={() => handleAnswer(option.isCorrect)}>
                {option.text}
              </div>
            ))}
          </div>
          {isAnswered && (
            <div className={styles.feedback}>
              <div className={styles.answer}>{`The correct answer is: ${currentQuestion.options.find((option) => option.isCorrect).text}`}</div>
              <label className={styles.label}>
                Answer: <input type="text" value={answer} onChange={(event) => setAnswer(event.target.value)} />
              </label>
              <button className={styles.button} onClick={handleNext}>
                Next
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
