import React, { useState } from 'react';
import toast, { Toaster } from 'react-hot-toast';
import styles from '../styles/questionnaire.module.css';


export default function Quiz() {
  const [questionIndex, setQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [isAnswered, setIsAnswered] = useState(false);
  const [answer, setAnswer] = useState('');
  const questions = [
    {
      text: 'Am the life of the party',
      options: [
        { text: '1', value: 1 },
        { text: '2', value: 2 },
        { text: '3', value: 3 },
        { text: '4', value: 4 },
        { text: '5', value: 5 },
      ],
    },
    {
      text: 'Feel little concern for others',
      options: [
        { text: '1', value: 1 },
        { text: '2', value: 2 },
        { text: '3', value: 3 },
        { text: '4', value: 4 },
        { text: '5', value: 5 },
      ],
    },
  ];

  const handleAnswer = (value) => {
    setScore(score + value);
    setIsAnswered(true);
  };

  const handleNext = () => {
    setIsAnswered(false);
    setAnswer('');
    if (questionIndex === questions.length - 1) {
      toast.success(`Your score is ${score}`);
    } else {
      setQuestionIndex(questionIndex + 1);
    }
  };

  const currentQuestion = questions[questionIndex];

  return (
    <div className="container mx-auto">
      <Toaster position="top-center" reverseOrder={false} />
      <div className="flex justify-center items-center" style={{ marginTop: '3rem', marginBottom: '3rem' }}>
        <div className={styles.quiz}>
          <h5 className={styles.question}>{currentQuestion.text}</h5>
          <div className={styles.options}>
            {currentQuestion.options.map((option, index) => (
              <div key={index} className={styles.option} onClick={() => handleAnswer(option.value)}>
                {option.text}
              </div>
            ))}
          </div>
          {isAnswered && (
            <div className={styles.feedback}>
              <div className={styles.answer}>{`Your answer: ${score - currentQuestion.options.find((option) => option.value === answer)?.value}`}</div>
              <button className={styles.button} onClick={handleNext}>
                {questionIndex === questions.length - 1 ? 'Finish' : 'Next'}
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
